<!DOCTYPE html>
<html lang="zh">
<head>
    <meta charset="utf-8" />
    <title>Simple example - Editor.md examples</title>
    <link rel="stylesheet" href="public/admin/md/examples/css/style.css" />
    <link rel="stylesheet" href="public/admin/md/css/editormd.css" />
    <link rel="shortcut icon" href="https://pandao.github.io/editor.md/favicon.ico" type="image/x-icon" />
</head>
<body>
<div id="layout">
   
    <form action="index.php?m=admin&c=release&a=release" method="post">
	 <header>
        <h2>标题：<input type="text" name="title" style="height:30px;width:350px;"></h2>
    </header>
	<br />
	<h3>正文：</h3>
        <div id="test-editormd">
		
                <textarea name="content" style="display:none;">


</textarea>
        </div>
        <input type="submit" value="发布" style="height:30px;width:80px; margin-left:480px;" />
    </form>

</div>
<script src="public/admin/md/examples/js/jquery.min.js"></script>
<script src="public/admin/md/editormd.min.js"></script>
<script type="text/javascript">
    var testEditor;

    $(function() {
        testEditor = editormd("test-editormd", {
            width   : "90%",
            height  : 640,
            syncScrolling : "single",
            path    : "public/admin/md/lib/"
        });

        /*
         // or
         testEditor = editormd({
         id      : "test-editormd",
         width   : "90%",
         height  : 640,
         path    : "../lib/"
         });
         */
    });
</script>
</body>
</html>